    srand(time(NULL));

    int i = rand() % (sizeof(words) / sizeof(words[0]));

    // printf("[DEBUG] word: %s\n", words[i]);

    char hidden[100] = {0};

    int wordLen = strlen(words[i]);
    int charLen = wordLen;

    for (int x = 0; x < wordLen; x++) {
        hidden[x] = '_';
        if (words[i][x] == ' ') {
            charLen--;
            hidden[x] = ' ';
        }
    }

    int guessed = 0;
    int lost = 0;
    int letters[256] = {0};

    while (!(guessed == charLen) && !(lost == 6)) {

        printf("%s\n", hangmanArt[lost]);
        printHidden(hidden, wordLen);
        printf("guess a letter: ");
        fflush(stdout);

        char c = getchar();
        getchar();
        // printf("[DEBUG] char: %c\n", c);

        if (c > 'z' || c < 'a') {
            printf("you can only guess lowercase letters...\n");
            fflush(stdout);
        } else {
            int g = 0;

            for (int j = 0; j < wordLen; j++) {
                if (lower_case(words[i][j]) == c && !letters[c]) {
                    hidden[j] = c;
                    g = 1;
                    guessed++;
                }
            }

            if (!g) {
                printf("%c is not a new letter or not in the word!\n", c);
                fflush(stdout);
                //lost++;
                lost = 6; // otherwise the game is too easy ...
                if (lost == 6) {
                    printf("%s\n", hangmanArt[lost]);
                    printf("you lost!\nthe word was: %s\n", words[i]);
                    fflush(stdout);
                }
            } else {
                letters[c] = 1;
                if (guessed == charLen) {
                    printf("you won!\nthe word was: %s\n", words[i]);
                    fflush(stdout);
                    win();
                } else {
                    printf("%c was correct, here is the updated stuff:\n", c);
                    fflush(stdout);
                }
            }
        }
    }

    return 0;
